            <Users className="h-5 w-5" />
            Member Directory
          </CardTitle>
          <div className="relative">
            <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
            <Input
              placeholder="Search members, companies, or roles..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10"
            />
          </div>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredUsers.map((user: any) => (
              <Card key={user.id} className="p-6 hover:shadow-lg transition-shadow">
                <div className="flex items-start space-x-4">
                  {/* Profile Avatar */}
                  <div className="w-16 h-16 rounded-full bg-gray-200 flex items-center justify-center text-lg font-semibold text-gray-700 flex-shrink-0">
                    {user.profile_image ? (
                      <img 
                        src={user.profile_image} 
                        alt={`${user.first_name} ${user.last_name}`}
                        className="w-16 h-16 rounded-full object-cover"
                      />
                    ) : (
                      getInitials(user.first_name, user.last_name)
                    )}
                  </div>

                  {/* User Info */}
                  <div className="flex-1 min-w-0">
                    <h3 className="text-lg font-semibold text-gray-900 mb-1">
                      {user.first_name} {user.last_name}
                    </h3>

                    {/* Company */}
                    {user.company && (
                      <p className="text-sm text-gray-600 flex items-center gap-1 mb-1">
                        <Building2 className="h-4 w-4" />
                        {user.company}
                      </p>
                    )}

                    {/* Job Title */}
                    <div className="mb-3">
                      <span className="text-sm font-medium text-gray-900">
                        {user.job_title || formatRole(user.role)}
                      </span>
                    </div>

                    {/* Bio */}
                    {user.bio && (
                      <p className="text-sm text-gray-600 mb-4 line-clamp-3 leading-relaxed">
                        {user.bio}
                      </p>
                    )}

                    {/* Action Buttons */}
                    <div className="flex items-center gap-2">
                      <Button 
                        size="sm" 
                        variant="outline" 
                        className="text-xs h-8 px-3"
                        onClick={() => window.open(`mailto:${user.email}`, '_blank')}
                      >
                        <Mail className="h-3 w-3 mr-1" />
                        Email
                      </Button>
                      {user.linkedin_url && (
                        <Button 
                          size="sm" 
                          variant="outline" 
                          className="text-xs h-8 px-3"
                          onClick={() => window.open(user.linkedin_url, '_blank')}
                        >
                          <Linkedin className="h-3 w-3 mr-1" />
                          LinkedIn
                        </Button>
                      )}
                    </div>
                  </div>
                </div>
              </Card>
            ))}
          </div>
          {filteredUsers.length === 0 && (
            <div className="text-center py-8 text-gray-500">
              No members found matching your search.
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

