import { useState } from "react";
import { useAuth } from "@/hooks/use-auth";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Carousel, CarouselContent, CarouselItem, CarouselNext, CarouselPrevious } from "@/components/ui/carousel";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Separator } from "@/components/ui/separator";
import { Link } from "wouter";
import { 
  Coffee, 
  Calendar, 
  CreditCard, 
  Clock, 
  MapPin, 
  Star, 
  Gift, 
  Bell,
  X,
  Download,
  Building,
  Users,
  TrendingUp,
  FileText,
  ChevronLeft,
  ChevronRight,
  Receipt,
  DollarSign,
  Utensils,
  CheckCircle
} from "lucide-react";
import { CafeOrder, MeetingBooking, Announcement } from "@/lib/types";
import { CreditAnimation, useCreditAnimation } from "@/components/ui/credit-animation";
import { formatPriceWithCurrency } from "@/lib/format-price";

export default function Dashboard() {
  const { user } = useAuth();
  // Removed dismissedAnnouncements state - moved to Community page
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [roomBookingsPage, setRoomBookingsPage] = useState(1);
  const roomBookingsPerPage = 5;
  const [cafeOrdersPage, setCafeOrdersPage] = useState(1);
  const cafeOrdersPerPage = 5;

  const downloadCafePDF = async () => {
    try {
      const params = new URLSearchParams();
      if (startDate) params.append('start_date', startDate);
      if (endDate) params.append('end_date', endDate);

      const response = await fetch(`/api/cafe/orders/pdf?${params}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/pdf',
        },
      });

      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `cafe-orders-${startDate || 'all'}-${endDate || 'all'}.pdf`;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);
      } else {
        console.error('Failed to download PDF');
      }
    } catch (error) {
      console.error('Error downloading caf√© PDF:', error);
    }
  };

  const downloadRoomPDF = async () => {
    try {
      const params = new URLSearchParams();
      if (startDate) params.append('start_date', startDate);
      if (endDate) params.append('end_date', endDate);

      const response = await fetch(`/api/bookings/pdf?${params}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/pdf',
        },
      });

      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `room-bookings-${startDate || 'all'}-${endDate || 'all'}.pdf`;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);
      } else {
        console.error('Failed to download PDF');
      }
    } catch (error) {
      console.error('Error downloading room PDF:', error);
    }
  };

  // Announcements moved to Community page

  const { data: recentOrders = [] } = useQuery<CafeOrder[]>({
    queryKey: ["/api/cafe/orders"],
    enabled: !!user,
    staleTime: 0, // Always fetch fresh data
    gcTime: 0, // Don't cache results
  });

  const { data: recentBookings = [] } = useQuery<MeetingBooking[]>({
    queryKey: ["/api/bookings"],
    enabled: !!user,
  });

  const { data: dailySpecials = [] } = useQuery({
    queryKey: ["/api/menu/daily-specials", user?.site],
    enabled: !!user,
  });

  const { data: availableRooms = [] } = useQuery({
    queryKey: ["/api/rooms/available", user?.site],
    enabled: !!user,
  });

  if (!user) return null;

  const availableCredits = user.credits - user.used_credits;
  const creditsUsedPercentage = user.credits > 0 ? Math.min((user.used_credits / user.credits) * 100, 100) : 0;
  const isNegativeBalance = availableCredits < 0;

  // Credit animation hook
  const { previousCredits, showAnimation } = useCreditAnimation(availableCredits);

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8 space-y-8">
      {/* Welcome Section */}
      <div>
        <h2 className="text-3xl font-bold text-gray-900 mb-2">
          Welcome back, {user.first_name}!
        </h2>
        <p className="text-gray-600 flex items-center gap-2">
          <MapPin className="h-4 w-4" />
          {user.site === 'blue_area' ? 'Blue Area' : 'I-10'} Location
          {user.organization_id && (
            <>
              <Building className="h-4 w-4 ml-4" />
              Organization Member
            </>
          )}
        </p>
      </div>

      {/* Announcements moved to Community page */}

      {/* Quick Action Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <Card className="hover:shadow-lg transition-shadow bg-gradient-to-br from-orange-50 to-amber-50 border-orange-200">
          <CardHeader className="pb-3">
            <CardTitle className="flex items-center gap-2 text-orange-800">
              <Coffee className="h-5 w-5" />
              Order from Caf√©
            </CardTitle>
          </CardHeader>
          <CardContent className="flex flex-col">
            <p className="text-orange-700 text-sm mb-3">Fresh coffee, snacks & meals</p>
            {isNegativeBalance && <div className="flex-1 mb-8"></div>}
            <Link href="/cafe">
              <Button className="w-full bg-orange-600 hover:bg-orange-700">
                View Menu
              </Button>
            </Link>
          </CardContent>
        </Card>

        <Card className="hover:shadow-lg transition-shadow bg-gradient-to-br from-green-50 to-emerald-50 border-green-200">
          <CardHeader className="pb-3">
            <CardTitle className="flex items-center gap-2 text-green-800">
              <Calendar className="h-5 w-5" />
              Book Meeting Room
            </CardTitle>
          </CardHeader>
          <CardContent className="flex flex-col">
            <p className="text-green-700 text-sm mb-3">Private spaces for meetings</p>
            {isNegativeBalance && <div className="flex-1 mb-8"></div>}
            <Link href="/rooms">
              <Button className="w-full bg-green-600 hover:bg-green-700">
                Book Now
              </Button>
            </Link>
          </CardContent>
        </Card>

        {/* Credits Widget */}
        <Card className={`bg-gradient-to-br ${isNegativeBalance ? 'from-red-50 to-orange-50 border-red-200' : 'from-green-50 to-emerald-50 border-green-200'}`}>
          <CardHeader className="pb-3">
            <CardTitle className={`flex items-center gap-2 ${isNegativeBalance ? 'text-red-800' : 'text-green-800'}`}>
              <CreditCard className="h-5 w-5" />
              Your Credits
              {isNegativeBalance && (
                <Badge variant="destructive" className="ml-2 text-xs">
                  Negative Balance
                </Badge>
              )}
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              <div className="flex justify-between text-sm">
                <span className={isNegativeBalance ? "text-red-700" : "text-green-700"}>
                  Used: {user.used_credits}
                </span>
                <span className="font-medium">
                  Available: <CreditAnimation 
                    currentCredits={availableCredits}
                    previousCredits={previousCredits}
                    showAnimation={showAnimation}
                    className={availableCredits < 0 ? "text-red-700" : "text-green-700"}
                  />
                </span>
              </div>
              <Progress 
                value={creditsUsedPercentage} 
                className={`h-2 ${isNegativeBalance ? 'bg-red-100' : ''}`}
              />
              <div className="flex justify-between items-center">
                <p className={`text-xs ${isNegativeBalance ? 'text-red-600' : 'text-green-600'}`}>
                  Credits Assigned: {user.credits}
                </p>
                {isNegativeBalance && (
                  <p className="text-xs text-red-600 font-medium">
                    Extra Usage: {Math.abs(availableCredits)} credits
                  </p>
                )}
              </div>
              {isNegativeBalance && (
                <Alert>
                  <DollarSign className="h-4 w-4" />
                  <AlertDescription className="text-sm">
                    Your account has a negative balance and will appear on your monthly invoice for billing.
                  </AlertDescription>
                </Alert>
              )}
            </div>
          </CardContent>
        </Card>
      </div>





      {/* Quick Stats & PDF Downloads */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <TrendingUp className="h-5 w-5" />
            Your Activity Summary
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="text-center p-4 bg-gray-50 rounded-lg">
              <p className="text-2xl font-bold text-gray-900">{recentOrders.length}</p>
              <p className="text-sm text-gray-600">Total Orders</p>
            </div>
            <div className="text-center p-4 bg-gray-50 rounded-lg">
              <p className="text-2xl font-bold text-gray-900">{recentBookings.length}</p>
              <p className="text-sm text-gray-600">Total Bookings</p>
            </div>
            <div className="text-center p-4 bg-gray-50 rounded-lg">
              <p className="text-2xl font-bold text-gray-900">{user.used_credits}</p>
              <p className="text-sm text-gray-600">Credits Used</p>
            </div>
          </div>


        </CardContent>
      </Card>

      {/* Transaction History */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <FileText className="h-5 w-5" />
            Transaction History & Bills
          </CardTitle>
        </CardHeader>
        <CardContent>
          <Tabs defaultValue="cafe" className="w-full">
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="cafe">Caf√© Orders</TabsTrigger>
              <TabsTrigger value="rooms">Room Bookings</TabsTrigger>
            </TabsList>

            <TabsContent value="cafe" className="space-y-4">
              <div className="space-y-4 mb-6">
                <div className="grid grid-cols-2 gap-3 sm:gap-4">
                  <div>
                    <Label htmlFor="cafe-start-date" className="text-sm font-medium">Start Date</Label>
                    <Input
                      type="date"
                      id="cafe-start-date"
                      value={startDate}
                      onChange={(e) => setStartDate(e.target.value)}
                      className="mt-1"
                    />
                  </div>
                  <div>
                    <Label htmlFor="cafe-end-date" className="text-sm font-medium">End Date</Label>
                    <Input
                      type="date"
                      id="cafe-end-date"
                      value={endDate}
                      onChange={(e) => setEndDate(e.target.value)}
                      className="mt-1"
                    />
                  </div>
                </div>
                <Button onClick={() => downloadCafePDF()} className="w-full sm:w-auto">
                  <Download className="h-4 w-4 mr-2" />
                  Download PDF Report
                </Button>
              </div>

              <div className="space-y-3">
                <div className="flex items-center justify-between">
                  <h4 className="font-semibold">Caf√© Orders</h4>
                  {recentOrders.length > cafeOrdersPerPage && (
                    <div className="flex items-center gap-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => setCafeOrdersPage(prev => Math.max(1, prev - 1))}
                        disabled={cafeOrdersPage === 1}
                      >
                        <ChevronLeft className="h-4 w-4" />
                      </Button>
                      <span className="text-sm text-gray-500">
                        {cafeOrdersPage} of {Math.ceil(recentOrders.length / cafeOrdersPerPage)}
                      </span>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => setCafeOrdersPage(prev => Math.min(Math.ceil(recentOrders.length / cafeOrdersPerPage), prev + 1))}
                        disabled={cafeOrdersPage >= Math.ceil(recentOrders.length / cafeOrdersPerPage)}
                      >
                        <ChevronRight className="h-4 w-4" />
                      </Button>
                    </div>
                  )}
                </div>
                {recentOrders
                  .slice((cafeOrdersPage - 1) * cafeOrdersPerPage, cafeOrdersPage * cafeOrdersPerPage)
                  .map((order) => (
                  <div key={order.id} className="flex items-center justify-between p-4 border rounded-lg">
                    <div className="flex items-center gap-3">
                      <Coffee className="h-5 w-5 text-orange-600" />
                      <div>
                        <p className="font-medium">Order #{order.id}</p>
                        <p className="text-sm text-gray-500">
                          {new Date(order.created_at).toLocaleDateString()} ‚Ä¢ {new Date(order.created_at).toLocaleTimeString()}
                        </p>
                        <p className="text-xs text-gray-400">
                          {order.items && order.items.length > 0 
                            ? order.items.map(item => 
                                item.quantity > 1 
                                  ? `${item.menu_item?.name} x${item.quantity}`
                                  : item.menu_item?.name
                              ).join(', ')
                            : 'No items'
                          }
                        </p>
                      </div>
                    </div>
                    <div className="text-right">
                      <p className="font-semibold">{formatPriceWithCurrency(parseFloat(order.total_amount) || 0)}</p>
                      <Badge 
                        variant={order.status === 'delivered' ? 'default' : 'secondary'}
                        className={
                          order.status === 'delivered' ? 'bg-green-100 text-green-800' :
                          order.status === 'ready' || order.status === 'preparing' ? 'bg-blue-100 text-blue-800' :
                          order.status === 'cancelled' ? 'bg-red-100 text-red-800' : ''
                        }
                      >
                        {order.status}
                      </Badge>
                    </div>
                  </div>
                ))}
                {recentOrders.length === 0 && (
                  <div className="text-center py-8 text-gray-500">
                    No caf√© orders found
                  </div>
                )}
              </div>
            </TabsContent>

            <TabsContent value="rooms" className="space-y-4">
              <div className="space-y-4 mb-6">
                <div className="grid grid-cols-2 gap-3 sm:gap-4">
                  <div>
                    <Label htmlFor="room-start-date" className="text-sm font-medium">Start Date</Label>
                    <Input
                      type="date"
                      id="room-start-date"
                      value={startDate}
                      onChange={(e) => setStartDate(e.target.value)}
                      className="mt-1"
                    />
                  </div>
                  <div>
                    <Label htmlFor="room-end-date" className="text-sm font-medium">End Date</Label>
                    <Input
                      type="date"
                      id="room-end-date"
                      value={endDate}
                      onChange={(e) => setEndDate(e.target.value)}
                      className="mt-1"
                    />
                  </div>
                </div>
                <Button onClick={() => downloadRoomPDF()} className="w-full sm:w-auto">
                  <Download className="h-4 w-4 mr-2" />
                  Download PDF Report
                </Button>
              </div>

              <div className="space-y-3">
                <div className="flex items-center justify-between">
                  <h4 className="font-semibold">Room Bookings</h4>
                  {recentBookings.length > roomBookingsPerPage && (
                    <div className="flex items-center gap-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => setRoomBookingsPage(prev => Math.max(1, prev - 1))}
                        disabled={roomBookingsPage === 1}
                      >
                        <ChevronLeft className="h-4 w-4" />
                      </Button>
                      <span className="text-sm text-gray-500">
                        {roomBookingsPage} of {Math.ceil(recentBookings.length / roomBookingsPerPage)}
                      </span>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => setRoomBookingsPage(prev => Math.min(Math.ceil(recentBookings.length / roomBookingsPerPage), prev + 1))}
                        disabled={roomBookingsPage >= Math.ceil(recentBookings.length / roomBookingsPerPage)}
                      >
                        <ChevronRight className="h-4 w-4" />
                      </Button>
                    </div>
                  )}
                </div>
                {recentBookings
                  .slice((roomBookingsPage - 1) * roomBookingsPerPage, roomBookingsPage * roomBookingsPerPage)
                  .map((booking) => (
                  <div key={booking.id} className="flex items-center justify-between p-4 border rounded-lg">
                    <div className="flex items-center gap-3">
                      <Calendar className="h-5 w-5 text-blue-600" />
                      <div>
                        <p className="font-medium">{booking.room?.name}</p>
                        <p className="text-sm text-gray-500">
                          {new Date(booking.start_time).toLocaleDateString()} ‚Ä¢ {new Date(booking.start_time).toLocaleTimeString()} - {new Date(booking.end_time).toLocaleTimeString()}
                        </p>
                        <p className="text-xs text-gray-400">
                          {Math.round((new Date(booking.end_time).getTime() - new Date(booking.start_time).getTime()) / (1000 * 60))} minutes
                        </p>
                      </div>
                    </div>
                    <div className="text-right">
                      <p className="font-semibold">{booking.credits_used} credits</p>
                      <Badge 
                        variant={booking.status === 'confirmed' ? 'default' : 'secondary'}
                        className={
                          booking.status === 'confirmed' ? 'bg-green-100 text-green-800' :
                          booking.status === 'cancelled' ? 'bg-red-100 text-red-800' : ''
                        }
                      >
                        {booking.status}
                      </Badge>
                    </div>
                  </div>
                ))}
                {recentBookings.length === 0 && (
                  <div className="text-center py-8 text-gray-500">
                    No room bookings found
                  </div>
                )}
              </div>
            </TabsContent>
          </Tabs>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/menu-management.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Plus, Edit2, Trash2, Menu, Ghost } from "lucide-react";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { SimpleMenuEdit } from "@/components/simple-menu-edit";
import { formatPriceWithCurrency } from "@/lib/format-price";
import type { MenuItem, MenuCategory } from "shared/schema";

export default function MenuManagement() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [editingMenuItem, setEditingMenuItem] = useState<any>(null);
  const [isSimpleEditOpen, setIsSimpleEditOpen] = useState(false);

  // Determine API endpoint based on user role
  const isAdmin = user?.role === 'calmkaaj_admin';
  const isCafeManager = user?.role === 'cafe_manager';
  // Both admins and cafe managers should see all items to manage both sites
  const apiEndpoint = (isAdmin || isCafeManager) ? '/api/admin/menu/items' : '/api/menu/items';

  // Fetch menu items
  const { data: menuItems = [], isLoading } = useQuery<MenuItem[]>({
    queryKey: [apiEndpoint],
    enabled: !!user && (isAdmin || isCafeManager)
  });

  // Fetch categories (admin and cafe managers get all categories to see both sites)
  const categoriesEndpoint = (isAdmin || isCafeManager) ? '/api/admin/menu/categories' : '/api/menu/categories';
  const { data: categories = [] } = useQuery<MenuCategory[]>({
    queryKey: [categoriesEndpoint],
    enabled: !!user && (isAdmin || isCafeManager)
  });

  // Create menu item mutation
  const createMenuItem = useMutation({
    mutationFn: async (menuItemData: any) => {
      return apiRequest('POST', '/api/menu/items', menuItemData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/menu/items"] });
      queryClient.invalidateQueries({ queryKey: ["/api/menu/items"] });
      toast({ title: "Menu item created successfully!" });
    },
    onError: (error: any) => {
      toast({ 
        title: "Failed to create menu item", 
        description: error.message || "Please try again.",
        variant: "destructive"
      });
    }
  });

  // Update menu item mutation
  const updateMenuItem = useMutation({
    mutationFn: async ({ itemId, updates }: { itemId: number; updates: any }) => {
      return apiRequest('PATCH', `/api/menu/items/${itemId}`, updates);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/menu/items"] });
      queryClient.invalidateQueries({ queryKey: ["/api/menu/items"] });
      toast({ title: "Menu item updated successfully!" });
    },
    onError: (error: any) => {
      toast({ 
        title: "Failed to update menu item", 
        description: error.message || "Please try again.",
        variant: "destructive"
      });
    }
  });

  // Toggle availability mutation (archive/unarchive)
  const toggleAvailability = useMutation({
    mutationFn: async ({itemId, currentStatus}: {itemId: number, currentStatus: boolean}) => {
      return apiRequest('PATCH', `/api/menu/items/${itemId}`, { is_available: !currentStatus });
    },
    onSuccess: (data, variables) => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/menu/items"] });
      queryClient.invalidateQueries({ queryKey: ["/api/menu/items"] });
      const newStatus = !variables.currentStatus;
      toast({ title: newStatus ? "Menu item made available!" : "Menu item archived (unavailable)!" });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error?.message || "Failed to update menu item",
        variant: "destructive",
      });
    },
  });

  // Force delete menu item mutation (actual deletion)
  const forceDeleteMenuItem = useMutation({
    mutationFn: async (itemId: number) => {
      return apiRequest('DELETE', `/api/menu/items/${itemId}/force`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/menu/items"] });
      queryClient.invalidateQueries({ queryKey: ["/api/menu/items"] });
      toast({ title: "Menu item permanently deleted!" });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error?.message || "Failed to delete menu item. Item may have existing orders.",
        variant: "destructive",
      });
    },
  });

  const handleEditMenuItem = (item: any) => {
    setEditingMenuItem(item);
    setIsSimpleEditOpen(true);
  };

  const handleAddMenuItem = () => {
    setEditingMenuItem(null);
    setIsSimpleEditOpen(true);
  };

  const handleSaveFromSimpleEdit = (itemData: any) => {
    if (itemData.id) {
      // Update existing item
      updateMenuItem.mutate({ itemId: itemData.id, updates: itemData });
    } else {
      // Create new item
      createMenuItem.mutate(itemData);
    }
    setIsSimpleEditOpen(false);
    setEditingMenuItem(null);
  };

  const handleToggleAvailability = (item: any) => {
    const action = item.is_available ? "archive (make unavailable)" : "make available";
    if (confirm(`Are you sure you want to ${action} this menu item?`)) {
      toggleAvailability.mutate({itemId: item.id, currentStatus: item.is_available});
    }
  };

  const handleForceDeleteMenuItem = (itemId: number) => {
    if (confirm("Are you sure you want to PERMANENTLY DELETE this menu item? This action cannot be undone!")) {
      forceDeleteMenuItem.mutate(itemId);
    }
  };

  if (isLoading) {
    return (
      <div className="container mx-auto p-6">
        <div className="flex items-center justify-center h-64">
          <div className="text-gray-500">Loading menu items...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold tracking-tight flex items-center gap-2">
            <Menu className="h-8 w-8" />
            Menu Management
          </h1>
          <p className="text-gray-600 mt-2">
            {isAdmin ? "Manage all caf√© menu items and pricing" : "Manage your caf√©'s menu items and pricing"}
          </p>
        </div>
        <Button onClick={handleAddMenuItem} className="bg-green-600 hover:bg-green-700">
          <Plus className="h-4 w-4 mr-2" />
          Add Menu Item
        </Button>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Menu Items</CardTitle>
        </CardHeader>
        <CardContent>
          {menuItems.length === 0 ? (
            <div className="text-center py-12">
              <Menu className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <p className="text-gray-500 text-lg">No menu items found</p>
              <p className="text-gray-400 text-sm mt-2">Get started by adding your first menu item</p>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Name</TableHead>
                    <TableHead>Price</TableHead>
                    <TableHead>Category</TableHead>
                    <TableHead>Site</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {menuItems.map((item) => (
                    <TableRow key={item.id}>
                      <TableCell>
                        <div>
                          <p className="font-medium text-gray-900">{item.name}</p>
                          <p className="text-sm text-gray-500">{item.description}</p>
                        </div>
                      </TableCell>
                      <TableCell>{formatPriceWithCurrency(item.price)}</TableCell>
                      <TableCell className="capitalize">
                        {categories.find((cat) => cat.id == item.category_id)?.name || "Uncategorized"}
                      </TableCell>
                      <TableCell>
                        <span className="capitalize">
                          {item.site === 'blue_area' ? 'Blue Area' : 
                           item.site === 'i_10' ? 'I-10' : 
                           item.site === 'both' ? 'Both Sites' :
                           item.site}
                        </span>
                      </TableCell>
                      <TableCell>
                        <div className="flex gap-1">
                          <Badge variant={item.is_available ? "default" : "secondary"}>
                            {item.is_available ? "Available" : "Unavailable"}
                          </Badge>
                          {item.is_daily_special && (
                            <Badge variant="destructive">Special</Badge>
                          )}
                        </div>
                      </TableCell>
                      <TableCell>
                        <div className="flex gap-2">
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => handleEditMenuItem(item)}
                            title="Edit menu item"
                          >
                            <Edit2 className="h-4 w-4" />
                          </Button>
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => handleToggleAvailability(item)}
                            title={item.is_available ? "Archive menu item (mark as unavailable)" : "Un-archive menu item (make available)"}
                          >
                            <Ghost className="h-4 w-4" />
                          </Button>
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => handleForceDeleteMenuItem(item.id)}
                            title="Permanently delete menu item"
                          >
                            <Trash2 className="h-4 w-4" />
                          </Button>
                        </div>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Simple Edit Dialog */}
      <SimpleMenuEdit
        isOpen={isSimpleEditOpen}
        onClose={() => {
          setIsSimpleEditOpen(false);
          setEditingMenuItem(null);
        }}
        item={editingMenuItem}
        onSave={handleSaveFromSimpleEdit}
      />
    </div>
  );
}

