import { Link, useLocation } from "wouter";
import { useAuth } from "@/hooks/use-auth";
import { Button } from "@/components/ui/button";
import { Building, Bell, User, Coffee, Calendar, CheckCircle, Clock, AlertCircle, LogOut } from "lucide-react";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
  DropdownMenuLabel,
} from "@/components/ui/dropdown-menu";
import { useQuery } from "@tanstack/react-query";
import { Badge } from "@/components/ui/badge";
import { useState } from "react";

export default function Navigation() {
  const { user, logout } = useAuth();
  const [location] = useLocation();
  const [notificationOpen, setNotificationOpen] = useState(false);

  if (!user) return null;

  const showCafeAndRooms = ["member_individual", "member_organization_admin", "calmkaaj_admin", "calmkaaj_team"].includes(user.role);
  const showCommunity = ["member_individual", "member_organization", "member_organization_admin", "calmkaaj_admin", "calmkaaj_team"].includes(user.role);
  const showOrganization = user.role === "member_organization_admin";
  const isCafeManager = user.role === "cafe_manager";
  const isAdminUser = user.role === "calmkaaj_admin" || user.role === "calmkaaj_team";

  const navigation = [
    { name: "Dashboard", href: "/", current: location === "/" },
    ...(isCafeManager ? [
      { name: "Create Order", href: "/create-order", current: location === "/create-order" },
      { name: "Billing & Transactions", href: "/billing-transactions", current: location === "/billing-transactions" },
      { name: "Menu Management", href: "/menu-management", current: location === "/menu-management" }
    ] : []),
    ...(showCafeAndRooms ? [{ name: "Cafe Order", href: "/cafe", current: location === "/cafe" }] : []),
    ...(showCafeAndRooms ? [{ name: "Meeting Rooms", href: "/rooms", current: location === "/rooms" }] : []),
    ...(showCommunity ? [{ name: "Community", href: "/community", current: location === "/community" }] : []),
    ...(showOrganization ? [{ name: "Organization", href: "/organization", current: location === "/organization" }] : []),
  ];

  const getInitials = (firstName: string, lastName: string) => {
    return `${firstName.charAt(0)}${lastName.charAt(0)}`.toUpperCase();
  };

  // Fetch recent orders for notifications
  const { data: recentOrders = [] } = useQuery({
    queryKey: ["/api/cafe/orders"],
    enabled: !!user,
  });

  // Fetch recent bookings for notifications
  const { data: recentBookings = [] } = useQuery({
    queryKey: ["/api/bookings"],
    enabled: !!user,
  });

  // Create notifications from recent orders and bookings
  const notifications = [
    ...recentOrders.slice(0, 3).map((order: any) => ({
      id: `order-${order.id}`,
      type: 'order',
      title: 'Caf√© Order Update',
      message: `Your ${order.items?.[0]?.name || 'order'} is ${order.status}`,
      time: new Date(order.created_at).toLocaleString(),
      status: order.status,
      icon: Coffee,
    })),
    ...recentBookings.slice(0, 3).map((booking: any) => ({
      id: `booking-${booking.id}`,
      type: 'booking',
      title: 'Room Booking',
      message: `${booking.room?.name || 'Room'} booking ${booking.status}`,
      time: new Date(booking.created_at).toLocaleString(),
      status: booking.status,
      icon: Calendar,
    })),
  ].slice(0, 5);

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'delivered':
      case 'confirmed':
        return 'bg-green-100 text-green-800';
      case 'preparing':
      case 'accepted':
        return 'bg-blue-100 text-blue-800';
      case 'pending':
        return 'bg-yellow-100 text-yellow-800';
      case 'cancelled':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'delivered':
      case 'confirmed':
        return CheckCircle;
      case 'preparing':
      case 'accepted':
        return Clock;
      case 'pending':
        return AlertCircle;
      case 'cancelled':
        return AlertCircle;
      default:
        return Clock;
    }
  };

  return (
    <header className="bg-white shadow-sm border-b border-gray-200">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          <div className="flex items-center">
            <Link href="/" className="cursor-pointer">
              <img 
                src="/logo-main.png" 
                alt="CalmKaaj" 
                className="h-8 w-auto hover:opacity-80 transition-opacity"
              />
            </Link>
          </div>

          <nav className="hidden md:flex space-x-8">
            {navigation.map((item) => (
              <Link 
                key={item.name} 
                href={item.href}
                data-onboarding={
                  item.href === '/cafe' ? 'cafe-link' :
                  item.href === '/rooms' ? 'rooms-link' :
                  item.href === '/community' ? 'community-link' :
                  undefined
                }
                className={`pb-4 font-medium transition-colors cursor-pointer ${
                  item.current
                    ? "text-primary border-b-2 border-primary"
                    : "text-gray-500 hover:text-gray-700"
                }`}
              >
                {item.name}
              </Link>
            ))}
          </nav>

          <div className="flex items-center space-x-4">


            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button 
                  variant="ghost" 
                  className="flex items-center space-x-2"
                  data-onboarding="profile-menu"
                >
                  <Avatar className="h-8 w-8">
                    {user.profile_image && (
                      <AvatarImage 
                        src={user.profile_image} 
                        alt={`${user.first_name} ${user.last_name}`}
                        className="object-cover"
                      />
                    )}
                    <AvatarFallback className="bg-primary text-white text-sm">
                      {getInitials(user.first_name, user.last_name)}
                    </AvatarFallback>
                  </Avatar>
                  <span className="text-sm font-medium text-gray-900 hidden sm:inline-block">
                    {user.first_name} {user.last_name}
                  </span>
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end">
                <DropdownMenuItem asChild>
                  <Link href="/profile">
                    <User className="mr-2 h-4 w-4" />
                    Profile
                  </Link>
                </DropdownMenuItem>
                <DropdownMenuSeparator />
                <DropdownMenuItem onClick={logout}>
                  <LogOut className="mr-2 h-4 w-4" />
                  Sign Out
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          </div>
        </div>
      </div>
    </header>
  );
}

