                  </Label>
                  <Switch
                    id={`room-${employee.id}`}
                    checked={employee.can_charge_room_to_org}
                    onCheckedChange={(checked) => 
                      handlePermissionToggle(employee.id, "can_charge_room_to_org", checked)
                    }
                    disabled={updatePermissionsMutation.isPending}
